import React, { useState, useEffect } from 'react';
import { useParams,Link} from 'react-router-dom';

function UpdateAd() {
  const { adId } = useParams(); 
 
  const [adDetails, setAdDetails] = useState({
    name: '',
    eventDate: '',
    city:'',
    category:'',
    photo:'',
    quantityTicket:''
  });

 
  useEffect(() => {
    
    fetch(`http://localhost:8080/ad/${adId}`, {
      method: 'GET',
      headers: {
        'Accept': 'application/json',
      },
    })
      .then(response => {
        if (!response.ok) {
          throw new Error(`Erreur HTTP! Statut: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        setAdDetails({
            name: data.name,
            eventDate: data.eventDate,
            city: data.city,
            category: data.category,
            photo: data.photo,
            quantityTicket: data.quantityTicket,
          });
        
      })
      .catch(error => {
        console.error('Erreur lors de la récupération des données:', error);
      });
  }, [adId]);

  const handleUpdate = () => {
  
    
    const updatedAdDetails = {
      id: adId,
      name: adDetails.name,
      eventDate: adDetails.eventDate,
      category: adDetails.category,
      city: adDetails.city,
      quantityTicket: adDetails.quantityTicket,
    };

    fetch(`http://localhost:8080/ad/${adId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedAdDetails),
    })
      .then(response => {
        if (!response.ok) {
          throw new Error(`Erreur HTTP! Statut: ${response.status}`);
        }
      
      })
      .catch(error => {
        console.error('Erreur lors de la mise à jour de l\'annonce:', error);
      });
  }
  return (
    <div>
      <h1>Modifier l'annonce</h1>
      <input
        type="text"
        value={updatedName}
        onChange={(e) => setUpdatedName(e.target.value)}
      />
       <input
        type="text"
        value={updatedPhoto}
        onChange={(e) => setUpdatedPhoto(e.target.value)}
      />
         <input
        type="text"
        value={updatedCity}
        onChange={(e) => setUpdatedCity(e.target.value)}
      />
         <input
        type="text"
        value={updatedCategory}
        onChange={(e) => setUpdatedCategory(e.target.value)}
      />
       <input
        type="text"
        value={updatedQuantityTicket}
        onChange={(e) => setUpdatedQuantityTicket(e.target.value)}
      />
 <input
        type="text"
        value={updatedEventDate}
        onChange={(e) => setUpdatedEventDate(e.target.value)}
      />
      
      <button onClick={handleUpdate}>Enregistrer</button>
      <Link to="/">Retour aux annonces</Link>
    </div>
  );
}

export default UpdateAd;
