import './App.css'
import Topbar from './components/topbar/Topbar';
import TopbarCo from './components/topbar/TopbarCo';
import {Routes, Route} from 'react-router-dom';
import Register from './view/register/register';
import TicketPage from './view/ticketpage/TicketPage';
import OrderConfirmation from './view/order/OrderConfirmation';
import OrderPayment from './view/orderpayment/OrderPayment';
import Home from './view/home/Home';
import Account from './view/account/Account';
import Login from './view/login/Login';
import { useEffect, useState } from 'react';
import Footer from './components/footer/Footer';
import UpdateAd from './components/dashboard/UpdateAd';
import AdDetails from './components/dashboard/AdDetails';
function App() {
  const [token, setToken] = useState(localStorage.getItem('authToken'));
  useEffect(() => {
    const checkAuthToken = () => {
      const authToken = localStorage.getItem('authToken');
      setToken(authToken);
    };

    checkAuthToken();

    const intervalId = setInterval(checkAuthToken, 1000);


    return () => {
      clearInterval(intervalId);
    };
  }, []);

  const logout = () => {
    localStorage.removeItem('authToken');
    setToken(null);
  };
  return (
    <div className="app-wrapper">
      {token ? <TopbarCo /> : <Topbar />}
      <Routes>
        <Route path="/">
          <Route index element={<Home />} />
          <Route path="/Login" element={<Login />} />
          <Route path="/Register" element={<Register />} />
          <Route path="/Account" element={<Account />} />
          <Route path="/TicketPage" element={<TicketPage />} />
            <Route path="/OrderConfirmation" element={<OrderConfirmation/>} />
            <Route path="/OrderConfirmation/:id" element={<OrderConfirmation/>} />
            <Route path="/OrderPayment" element={<OrderPayment/>} />
            <Route path="/update/:id" element={<UpdateAd/>} />
            <Route path="/ad-details/:adId" element={<AdDetails/>} />
            


           
        </Route>
      </Routes>
      <Footer className="footer" />
    </div>
  );
}

export default App;
