import React, { useState, useEffect } from 'react';
import { useParams,Link} from 'react-router-dom';

function UpdateAd() {
    const { adId } = useParams();
  const [adDetails, setAdDetails] = useState({
    name: '',
    eventDate: '',
    city: '',
    category: '',
    photo: '',
    quantityTicket: '',
  });

  useEffect(() => {
    fetch(`http://localhost:8080/ad/${adId}`, {
      method: 'GET',
      headers: {
        'Accept': 'application/json',
      },
    })
      .then(response => {
        if (!response.ok) {
          throw new Error(`Erreur HTTP! Statut: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        setAdDetails(data); // Remplit l'ensemble des données de l'annonce
      })
      .catch(error => {
        console.error('Erreur lors de la récupération des données:', error);
      });
  }, [adId]);


  const handleUpdate = () => {
    const updatedAdDetails = {
      id: adId,
      name: adDetails.name,
      eventDate: adDetails.eventDate,
      category: adDetails.category,
      city: adDetails.city,
      quantityTicket: adDetails.quantityTicket,
    };
  
    fetch(`http://localhost:8080/ad/${adId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedAdDetails),
    })
      .then(response => {
        if (!response.ok) {
          throw new Error(`Erreur HTTP! Statut: ${response.status}`);
        }
        // Vous pouvez effectuer des actions supplémentaires ici après la mise à jour
      })
      .catch(error => {
        console.error('Erreur lors de la mise à jour de l\'annonce:', error);
      });
  }
  const handleChange = (e) => {
    const { name, value } = e.target;
    setAdDetails((prevDetails) => ({
      ...prevDetails,
      [name]: value,
    }));
  };

  return (
    <div>
      <h1>Modifier l'annonce</h1>
      <form>
        <label>name :</label>
        <input
  type="text"
  value={adDetails.name}
  onChange={(e) => setAdDetails((prevDetails) => ({ ...prevDetails, name: e.target.value }))}
/>
        <label>Date d'événement :</label>
        <input
          type="text"
          value={adDetails.eventDate}
          onChange={(e) => setAdDetails({ ...adDetails, eventDate: e.target.value })}
        />
        <label>Ville :</label>
        <input
          type="text"
          value={adDetails.city}
          onChange={(e) => setAdDetails({ ...adDetails, city: e.target.value })}
        />
        <label>Catégorie :</label>
        <input
          type="text"
          value={adDetails.category}
          onChange={(e) => setAdDetails({ ...adDetails, category: e.target.value })}
        />
        <label>Photo :</label>
        <input
          type="text"
          value={adDetails.photo}
          onChange={(e) => setAdDetails({ ...adDetails, photo: e.target.value })}
        />
        <label>Quantité de billets :</label>
        <input
          type="text"
          value={adDetails.quantityTicket}
          onChange={(e) => setAdDetails({ ...adDetails, quantityTicket: e.target.value })}
        />
        <button onClick={handleUpdate}>Enregistrer</button>
      </form>
      <Link to="/">Retour aux annonces</Link>
    </div>
  );
}

export default UpdateAd;
