// AdDetails.js

import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';

function AdDetails({ ad, onDeleteClick }) {
  const { adId } = useParams();
  const [adDetails, setAdDetails] = useState({
    name: '',
    eventDate: '',
    category: '',
    city: '',
    // Ajoutez d'autres détails d'annonce ici
  });

  useEffect(() => {
    // Effectuez une requête pour obtenir les détails de l'annonce par son ID
    fetch(`http://localhost:8080/ad/${adId}`)
      .then((response) => response.json())
      .then((data) => {
        setAdDetails(data);
      })
      .catch((error) => {
        console.error('Erreur lors de la récupération des détails de l\'annonce:', error);
      });
  }, [adId]);

  return (
    <div>
      <h1>Détails de l'annonce</h1>
      <p>Nom de l'annonce : {adDetails.name}</p>
      <p>Date de l'événement : {adDetails.eventDate}</p>
      <p>Catégorie : {adDetails.category}</p>
      <p>Ville : {adDetails.city}</p>
    
      <button onClick={() => onDeleteClick(ad.id)}>Supprimer</button>
      <Link to={`/update/${ad.id}`}>Mettre à jour</Link>
    </div>
  );
}

export default AdDetails;
