import React from 'react';
import { Link } from 'react-router-dom'; // Vous devrez configurer la navigation React Router pour gérer les redirections

const TicketPage = ({ ticket }) => {
  return (
    <div>
      <h1>Détails du ticket</h1>
      <p>Nom du ticket : {ticket.name}</p>
      <p>Prix : {ticket.price} €</p>
      <p>Description : {ticket.description}</p>

      <Link to={`/confirmation/${ticket.id}`}> {/* Redirige vers la page de confirmation avec l'ID du ticket */}
        <button>Commander</button>
      </Link>
    </div>
  );
};

export default TicketPage;
