// AdDetails.js
import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom'; // Si vous utilisez React Router

function AdDetails() {
  const { adId } = useParams(); // Obtenez l'ID de l'annonce depuis l'URL
  const [ad, setAd] = useState(null);

  useEffect(() => {
    // Effectuez la requête pour récupérer les détails de l'annonce en utilisant adId
    fetch(`http://localhost:8080/ad/${adId}`)
      .then((response) => response.json())
      .then((data) => {
        setAd(data);
      })
      .catch((error) => {
        console.error('Erreur lors de la récupération des détails de l\'annonce:', error);
      });
  }, [adId]);

  if (!ad) {
    // Affichez un message de chargement ou une indication que les détails sont en cours de chargement
    return <div>Chargement des détails de l'annonce...</div>;
  }

  return (
    <div>
      <h2>Détails de l'annonce</h2>
      <p>Nom de l'annonce : {ad.name}</p>
      <p>Lieu : {ad.city}</p>
      <p>Catégorie : {ad.category}</p>
      {/* Affichez d'autres détails de l'annonce ici */}
    </div>
  );
}

export default AdDetails;
