import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';

function UpdateAd() {
  const { adId } = useParams();
  const [adDetails, setAdDetails] = useState({
    name: '',
    eventDate: '',
    city: '',
    category: '',
    photo: '',
  });

  useEffect(() => {
    // Récupérez les détails de l'annonce par son ID depuis votre API
    fetch(`http://localhost:8080/api/ad/${adId}`)
      .then(response => response.json())
      .then(data => {
        setAdDetails(data);
      })
      .catch(error => {
        console.error('Erreur lors de la récupération des données de l\'annonce:', error);
      });
  }, [adId]);

  const handleUpdate = () => {
    // Envoyez les données mises à jour au backend pour mettre à jour l'annonce
    fetch(`http://localhost:8080/ad/${adId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(adDetails),
    })
      .then(response => {
        if (response.ok) {
          // Redirigez l'utilisateur vers la page de ventes après la mise à jour
          window.location.href = '/ventes';
        } else {
          console.error('Erreur lors de la mise à jour de l\'annonce');
        }
      })
      .catch(error => {
        console.error('Erreur lors de la mise à jour de l\'annonce:', error);
      });
  }

  return (
    <div>
      <h1>Modifier l'annonce</h1>
      <form>
        <label>Titre :</label>
        <input
          type="text"
          value={adDetails.name}
          onChange={(e) => setAdDetails({ ...adDetails, name: e.target.value })}
        />
        <label>Date de l'événement :</label>
        <input
          type="text"
          value={adDetails.eventDate}
          onChange={(e) => setAdDetails({ ...adDetails, eventDate: e.target.value })}
        />
        <label>Ville :</label>
        <input
          type="text"
          value={adDetails.city}
          onChange={(e) => setAdDetails({ ...adDetails, city: e.target.value })}
        />
        <label>Catégorie :</label>
        <input
          type="text"
          value={adDetails.category}
          onChange={(e) => setAdDetails({ ...adDetails, category: e.target.value })}
        />
        <label>Photo :</label>
        <input
          type="text"
          value={adDetails.photo}
          onChange={(e) => setAdDetails({ ...adDetails, photo: e.target.value })}
        />
        <button onClick={handleUpdate}>Enregistrer</button>
      </form>
      <Link to="/ventes">Retour aux ventes</Link>
    </div>
  );
}

export default UpdateAd;
