import React, { useState } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import './orderConfirmation.css'
import { Link } from 'react-router-dom';
import axios from 'axios';

const ConfirmationPage = () => {
  const { ticketId } = useParams();
  const history = useHistory(); // Permet de naviguer vers la page de paiement après la création de la commande

  const handleProceedToPayment = async () => {
    try {
      // Créez un objet orderTicket avec les données nécessaires
      const orderTicket = {
        // Ajoutez les détails de la commande ici, par exemple, le ticketId
        ticketId,
        // Ajoutez d'autres détails si nécessaires
      };

      // Effectuez une requête POST pour créer la commande
      const response = await axios.post('/votre-api/creationorder', orderTicket);

      // Si la création de la commande est réussie, naviguez vers la page de paiement
      if (response.status === 201) {
        history.push(`/OrderPayment/${response.data.id}`); // Assurez-vous de récupérer l'ID de la commande depuis la réponse de l'API
      } else {
        // Gérez les erreurs ici si nécessaire
        console.error('Erreur lors de la création de la commande.');
      }
    } catch (error) {
      // Gérez les erreurs ici
      console.error('Erreur lors de la création de la commande :', error);
    }
  };

  return (
    <>
      <div className="main">
        <div className="orderTitle">
          <h1> Récapitulatif de la commande :</h1>
        </div>
        <div className="orderContent">
          <p>Vous souhaitez acheter le ticket #{ticketId}</p>
          <Link to={`/OrderPayment/`}>
          <button className="button" onClick={handleProceedToPayment}>
            Passez à l'étape du paiement
          </button>
          </Link>
        </div>
      </div>
    </>
  );
};

export default ConfirmationPage;
