import React, { useState, useEffect } from 'react';
import { useParams ,useHistory} from 'react-router-dom';

function UpdateAd() {
  const { adId } = useParams(); // Récupérez l'ID de l'annonce à partir de l'URL
  const [updatedName, setUpdatedName] = useState('');
  const [updatedEventDate, setUpdatedEventDate] = useState('');
  const [updatedCity, setUpdatedCity] = useState('');
  const [updatedPhoto, setUpdatedPhoto] = useState('');
  const [updatedCategory, setUpdatedCategory] = useState('');
  const [updatedQuantityTicket, setUpdatedQuantityTicket] = useState('');
  const history = useHistory();
  useEffect(() => {
    // Récupérez les détails de l'annonce à partir de l'API en utilisant l'ID
    fetch(`http://localhost:8080/ad/${adId}`, {
      method: 'GET',
      headers: {
        'Accept': 'application/json',
      },
    })
      .then(response => {
        if (!response.ok) {
          throw new Error(`Erreur HTTP! Statut: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
      
        setUpdatedName(data.name);
        setUpdatedEventDate(data.eventDate);
        setUpdatedCity(data.city);
        setUpdatedPhoto(data.photo);
        setUpdatedCategory(data.category);
        setUpdatedQuantityTicket (data.quantityTicket);
 
 
      })
      .catch(error => {
        console.error('Erreur lors de la récupération des données:', error);
      });
  }, [adId]);

  const handleUpdate = () => {
    const updatedAd = {
      id: adId,
      name: updatedName,
      eventDate: updatedEventDate,
    };

    fetch(`http://localhost:8080/ad/${adId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedAd),
    })
      .then(response => {
        if (!response.ok) {
          throw new Error(`Erreur HTTP! Statut: ${response.status}`);
        }
        // Redirigez l'utilisateur vers la page des annonces après la mise à jour
        history.push('/');
      })
      .catch(error => {
        console.error('Erreur lors de la mise à jour de l\'annonce:', error);
      });
  }

  return (
    <div>
      <h1>Modifier l'annonce</h1>
      <input
        type="text"
        value={updatedName}
        onChange={(e) => setUpdatedName(e.target.value)}
      />
       <input
        type="text"
        value={updatedPhoto}
        onChange={(e) => setUpdatedPhoto(e.target.value)}
      />
         <input
        type="text"
        value={updatedCity}
        onChange={(e) => setUpdatedCity(e.target.value)}
      />
         <input
        type="text"
        value={updatedCategory}
        onChange={(e) => setUpdatedCategory(e.target.value)}
      />
       <input
        type="text"
        value={updatedQuantityTicket}
        onChange={(e) => setUpdatedQuantityTicket(e.target.value)}
      />
 <input
        type="text"
        value={updatedEventDate}
        onChange={(e) => setUpdatedEventDate(e.target.value)}
      />
      
      <button onClick={handleUpdate}>Enregistrer</button>
    </div>
  );
}

export default UpdateAd;
