import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';

function UpdateAd() {
  const { id } = useParams();
  const [ad, setAd] = useState({
    name: '',
    eventDate: '',
    city: '',
    category: '',
    photo: '',
  });
  const [formValues, setFormValues] = useState({});
  const [updateStatus, setUpdateStatus] = useState('');
  const [deleteStatus, setDeleteStatus] = useState('');
  const [showDetails, setShowDetails] = useState(false);

  useEffect(() => {
    fetch(`http://localhost:8080/ad/${id}`)
      .then((response) => response.json())
      .then((data) => {
        setAd(data);
      })
      .catch((error) => {
        console.error('Erreur lors de la récupération des détails de l\'annonce:', error);
      });
  }, [id]);

  const handleUpdate = () => {
    // Code de mise à jour ici
  };

  const handleDelete = () => {
    // Code de suppression ici
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues({
      ...formValues,
      [name]: value,
    });
  };

  return (
    <div className="ad-update">
      <h1>Mise à jour de l'annonce</h1>
      <form>
        {/* Vos champs de mise à jour ici */}
        <button type="button" onClick={handleUpdate}>
          Enregistrer
        </button>
        <button type="button" onClick={handleDelete}>
          Supprimer
        </button>
        <button type="button" onClick={() => setShowDetails(!showDetails)}>
          {showDetails ? 'Masquer Détails' : 'Afficher Détails'}
        </button>
        {showDetails && (
          <div>
            {/* Affichez les détails de l'annonce ici */}
            <p>Nom: {ad.name}</p>
            <p>Date de l'événement: {ad.eventDate}</p>
            <p>Ville: {ad.city}</p>
            <p>Catégorie: {ad.category}</p>
            <p>Photo: {ad.photo}</p>
          </div>
        )}
        <Link to={`/ad-details/${id}`}>Retour aux détails de l'annonce</Link>
      </form>
      {updateStatus && <p>{updateStatus}</p>}
      {deleteStatus && <p>{deleteStatus}</p>}
    </div>
  );
}

export default UpdateAd;
