// AdUpdate.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function UpdateAd() {
  const { id } = useParams();
  const [ad, setAd] = useState({
    name: '',
    city: '',
    // Autres champs de l'annonce
  });
  const [updateStatus, setUpdateStatus] = useState('');

  useEffect(() => {
    // Effectuez une requête pour obtenir les détails de l'annonce à mettre à jour en utilisant l'ID
    // Utilisez fetch ou Axios pour récupérer les données du serveur
    fetch(`http://localhost:8080/ad/${id}`)
      .then((response) => response.json())
      .then((data) => {
        setAd(data);
      })
      .catch((error) => {
        console.error('Erreur lors de la récupération des détails de l\'annonce:', error);
      });
  }, [id]);

  const handleUpdate = () => {
    // Effectuez une requête pour mettre à jour l'annonce avec les détails de ad
    // Utilisez fetch ou Axios pour envoyer les données au serveur
    fetch(`http://localhost:8080/ad/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(ad),
    })
      .then((response) => {
        if (response.ok) {
          setUpdateStatus('Mise à jour réussie');
        } else {
          setUpdateStatus('Erreur lors de la mise à jour de l\'annonce');
        }
      })
      .catch((error) => {
        setUpdateStatus('Erreur lors de la mise à jour de l\'annonce: ' + error.message);
      });
  };

  return (
    <div className="ad-update">
      <h1>Mise à jour de l'annonce</h1>
      <form>
        <input
          type="text"
          name="name"
          value={ad.name}
          onChange={(e) => setAd({ ...ad, name: e.target.value })}
        />
        <input
          type="text"
          name="city"
          value={ad.city}
          onChange={(e) => setAd({ ...ad, city: e.target.value })}
        />
        {/* Autres champs de mise à jour */}
        <button onClick={handleUpdate}>Mettre à jour</button>
      </form>
      {updateStatus && <p>{updateStatus}</p>}
    </div>
  );
}

export default UpdateAd;
